bool TestMap::IsCol(TestRect testRect, TestRect& testChipRect)
{
	for (int x = 0; x < kChipNumX; x++)
	{
		for (int y = 0; y < kChipNumY; y++)
		{
			// ï«à»äOÇ∆ÇÕìñÇΩÇÁÇ»Ç¢
			if (kChipSetDate[y][x] == 0) continue;

			int chipLeft = kChipWidth * x;
			int chipRight = chipLeft + kChipWidth;
			int chipTop = kChipHeight * y;
			int chipBottom = chipTop + kChipHeight;

			if (chipLeft > testRect.right) continue;
			if (chipTop > testRect.bottom) continue;
			if (chipRight < testRect.left) continue;
			if (chipBottom < testRect.top) continue;

			testChipRect.left = chipLeft;
			testChipRect.right = chipRight;
			testChipRect.top = chipTop;
			testChipRect.bottom = chipBottom;

			return true;
		}
	}
    return false;
}
